        $("<p class='desSpe'>缓冲区已全满</p>").insertBefore($("#des p:first"));
        return;
      }
      item = this.produceItem();
      this.data.empty = this.p(this.data.empty);
      this.data.mutex = this.p(this.data.mutex);
